import { useRef } from 'react';
import { useOutsidClick } from '../../hooks/useOutsideClick';

const Modal = ({ showModal, setShowModal }) => {
  const modalRef = useRef();

  useOutsidClick(modalRef,() => {
    setShowModal(false)
  });

  return(
    <div>
      {showModal && (
        <div ref={modalRef}>이거슨모달</div>
      )}
    </div>
  )
}

export default Modal
