import { useEffect, useRef, useState } from "react";
import Modal from "../components/modal/Modal";
import './list.scss';

const List = () => {
  const [count, setCount] = useState(0); // useState는 react에서 제공하는 함수
  const [showModal, setShowModal] = useState(false);
  const titRef = useRef(null);

  const increase = () => {
    setCount(count => count+1)
  }

  const decrease = () => {
    setCount(count => count-1)
  }

  useEffect(() => {
    console.log('카운터 발생')
  }, [count])

  return (
    <div className="wrap_count">
      <h1 ref={titRef}>버튼 만들기</h1>
      <div className="box_count">
        <strong className="result_num">{count}</strong>
        <button type="button" onClick={decrease}>감소</button>
        <button type="button" onClick={increase}>증가</button>

        <button type="button" onClick={()=> { setShowModal(!showModal) }}>모달</button>
        {
          showModal && <Modal setShowModal={setShowModal} />
        }

      </div>
    </div>
  )
}

export default List;
