import { useRef, useState } from "react";
import { useOutsideClick } from "../../../hooks/useOutsideClick";
import './modal.scss';

const Modal = ({ activeIndex, modalList, closeModal, showModal }) => {
  // const [modalData, setModalData] = useState( {
  //   ...modalList
  // });
  const modalRef = useRef(null);
  const modalData = modalList.findIndex((item) => item.musicalClassId === activeIndex);

  useOutsideClick(modalRef, closeModal);

  console.log(modalData)
  console.log(activeIndex)

  return (
    <div className={`modal_layer ${showModal && "show"}`}>
      <div className="overlay_layer">
        <div ref={modalRef} className="cont_layer" tabIndex="-1" role="dialog" aria-modal="true">
          <div className="inner_layer">
            <strong className="title_layer">{modalList.activeIndex}</strong>
            <button type="button" className="btn_close"><span className="ico_close"></span></button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Modal;
